import React, { Fragment, useEffect, useRef, useState } from 'react'
import styles from './styles.module.css'
import imageCompression from 'browser-image-compression'
var Clipper = require('image-clipper')
var Crop = require('tinycrop')

const defaultBase64 =
  'data:image/png;base64,'

var options = {
  maxSizeMB: 0.1,
  maxWidthOrHeight: 1000,
  useWebWorker: true
}

const initiate = (compression, setCropDialog, setTempImage, file) => {
  setTempImage()
  setCropDialog(true)

  imageCompression(file, compression || options)
    .then(function (compressedFile) {
      var reader = new FileReader()
      reader.readAsDataURL(compressedFile)
      reader.onloadend = function () {
        setTempImage(reader.result)
      }
    })
    .catch(function (error) {
      console.log(error.message)
    })
}

const initCropWindow = (img, setCropRegion) => {
  if (img) {
    document.getElementById('ReactImgInputCropMountLocation').innerHTML = ''
    var crop = Crop.create({
      parent: document.getElementById('ReactImgInputCropMountLocation'),
      image: img,
      bounds: {
        width: '100%',
        height: '100%'
      },
      backgroundColors: ['transparent', 'transparent'],
      selection: {
        activeColor: 'crimson',
        aspectRatio: 1,
        minWidth: 100,
        minHeight: 100
      }
    })
    document.getElementById(
      'ReactImgInputCropMountLocation'
    ).style.backgroundColor = '#ccc'
    crop.on('change', (region) => setCropRegion(region))
  }
}

const cropImage = (
  image,
  region,
  setImage,
  setCropDialog,
  setLoading,
  setOutput
) => {
  setCropDialog(false)
  setLoading(true)
  const { x, y, height, width } = region
  Clipper(image, function () {
    this.crop(x, y, height, width).toDataURL(function (dataUrl) {
      setImage(dataUrl)
      setOutput && setOutput(dataUrl)
      setLoading(false)
    })
  })
}

export const ReactImgInput = ({ config, setOutput }) => {
  const { size, captureBtn, cropBtn, defaultImg, theme, compression } = config

  const input = useRef()
  const [Image, setImage] = useState()
  const [tempImage, setTempImage] = useState()
  const [cropDialog, setCropDialog] = useState(false)
  const [cropRegion, setCropRegion] = useState({})
  const [loading, setLoading] = useState(false)

  useEffect(() => {
    cropDialog && initCropWindow(tempImage, setCropRegion)
  }, [cropDialog, tempImage])

  return (
    <Fragment>
      <main
        id={styles.ReactImgInput}
        style={{ height: size || '150px', width: size || '150px' }}
      >
        <input
          type='file'
          ref={input}
          accept='image/*'
          onInput={(e) =>
            initiate(
              compression,
              setCropDialog,
              setTempImage,
              e.target.files[0]
            )
          }
        />
        <img
          src={Image || defaultImg || defaultBase64}
          onClick={() => (Image ? setCropDialog(true) : input.current.click())}
        />
        {loading ? (
          <aside />
        ) : !Image ? (
          <button
            onClick={() => input.current.click()}
            style={{
              backgroundColor: captureBtn.bg || '#3498db',
              color: captureBtn.color || '#fff'
            }}
          >
            <svg
              xmlns='http://www.w3.org/2000/svg'
              viewBox='0 0 24 24'
              fill='none'
              stroke='currentColor'
              strokeWidth='2.5'
              strokeLinecap='round'
              strokeLinejoin='round'
            >
              <line x1={12} y1={5} x2={12} y2={19} />
              <line x1={5} y1={12} x2={19} y2={12} />
            </svg>
          </button>
        ) : (
              <button
                onClick={() => {
                  setCropDialog(true)
                }}
                style={{
                  backgroundColor: cropBtn.bg || '#F4B230',
                  color: cropBtn.color || '#fff'
                }}
              >
                <svg
                  xmlns='http://www.w3.org/2000/svg'
                  viewBox='0 0 24 24'
                  fill='none'
                  stroke='currentColor'
                  strokeWidth={2}
                  strokeLinecap='round'
                  strokeLinejoin='round'
                >
                  <circle cx={12} cy={12} r={10} />
                  <line x1={22} y1={12} x2={18} y2={12} />
                  <line x1={6} y1={12} x2={2} y2={12} />
                  <line x1={12} y1={6} x2={12} y2={2} />
                  <line x1={12} y1={22} x2={12} y2={18} />
                </svg>
              </button>
            )}
      </main>
      {cropDialog && (
        <Modal
          theme={theme}
          close={() => {
            input.current.value = null
            setCropDialog(false)
          }}
          recapture={() => {
            input.current.value = null
            input.current.click()
            setCropDialog(false)
          }}
          save={() => {
            input.current.value = null
            cropImage(
              tempImage,
              cropRegion,
              setImage,
              setCropDialog,
              setLoading,
              setOutput
            )
          }}
        />
      )}
    </Fragment>
  )
}

const Modal = ({ close, recapture, save, theme }) => {
  return (
    <main id={styles.ReactImgInputCropModal}>
      <section
        style={{ backgroundColor: theme === 'dark' ? '#424242' : '#fff' }}
      >
        <h1 style={{ color: theme === 'dark' ? '#fff' : '#424242' }}>
          Crop Image
        </h1>
        <article id='ReactImgInputCropMountLocation'>
          <aside className='loader' />
          Compressing...
        </article>
        <div>
          <button
            onClick={close}
            style={{ color: theme === 'dark' ? '#fff' : 'crimson' }}
          >
            Cancel
          </button>
          <button
            onClick={recapture}
            style={{ color: theme === 'dark' ? '#fff' : '#F4B400' }}
          >
            Recapture
          </button>
          <button
            onClick={save}
            style={{ color: theme === 'dark' ? '#fff' : '#1976d2' }}
          >
            Save
          </button>
        </div>
      </section>
    </main>
  )
}
